string Aesen(string text, string secret) { // Input plaintext string plainText = &#34;This is a secret message.&#34;; // Generate key byte[] key = Encoding.ASCII.GetBytes(secret); // Encrypt using (AesCryptoServiceProvider aes = new AesCryptoServiceProvider()) { aes.KeySize = 128;//相应的填充16=&gt;128 32=&gt;256 aes.BlockSize = 128; aes.Key = key; aes.Padding = PaddingMode.PKCS7; aes.Mode = CipherMode.ECB; ICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, null); byte[] plainBytes = Encoding.UTF8.GetBytes(text); byte[] encryptedBytes = encryptor.TransformFinalBlock(plainBytes, 0, plainBytes.Length); // Output ciphertext string cipherText = Convert.ToBase64String(encryptedBytes); Console.WriteLine(&#34;Ciphertext: &#34; + cipherText); return cipherText; } } 网络图片的保存#async Task SImg(string url, string filePath) { using (var client = new HttpClient()) { using (var response = await client.GetAsync(url)) { using (var stream = await response.Content.ReadAsStreamAsync()) { using (var fileStream = new FileStream(filePath, FileMode.Create, FileAccess.Write)) { await stream.CopyToAsync(fileStream); } } } } Console.WriteLine(&#34;Image downloaded.&#34;); } 缺口坐标识别（待优化#因为有些图中包含多个缺口，所有准确率有待优化
int GetImgX(string bg_path, string tg_path) { Mat bg = Cv2.ImRead(bg_path); Mat tg = Cv2.ImRead(tg_path); Mat bg_gray = new Mat(); Mat tg_gray = new Mat(); Cv2.CvtColor(bg, bg_gray, ColorConversionCodes.BGR2GRAY); Cv2.CvtColor(tg, tg_gray, ColorConversionCodes.BGR2GRAY); int h = tg.Rows; int w = tg.Cols; Mat res = new Mat(); Cv2.MatchTemplate(bg_gray, tg_gray, res, TemplateMatchModes.CCoeffNormed); double min_val, max_val; Point min_loc, max_loc; Cv2.MinMaxLoc(res, out min_val, out max_val, out min_loc, out max_loc); Console.WriteLine(max_loc); return (int)max_loc.X; }

